mapper.insert:
insert into article (Id, Title, AuthorId, 
  CreateTime, OriginalType, MarkdownContent, 
  HtmlContent, MarkdownCatalog, HtmlCatalog, 
  ReadTimes, ThumbsUpTimes, IsDelete
  )
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{authorId,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{originalType,jdbcType=VARCHAR}, #{markdownContent,jdbcType=VARCHAR}, 
  #{htmlContent,jdbcType=VARCHAR}, #{markdownCatalog,jdbcType=VARCHAR}, #{htmlCatalog,jdbcType=VARCHAR}, 
  #{readTimes,jdbcType=INTEGER}, #{thumbsUpTimes,jdbcType=INTEGER}, #{isDelete,jdbcType=BIT}
  )
