mapper.update:
update tag
set UserId = #{userId,jdbcType=INTEGER},
  TagName = #{tagName,jdbcType=VARCHAR},
  CreateTime = #{createTime,jdbcType=TIMESTAMP}
where TagId = #{tagId,jdbcType=INTEGER}

mapper.select:
select 'true' as QUERYID,TagId, UserId, TagName, CreateTime
from tag
where TagId = #{tagId,jdbcType=INTEGER}
